<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Rotating Cube</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <style>
        body {
            margin: 0;
            overflow: hidden;
            background: #000;
        }
        canvas {
            display: block;
        }
    </style>
</head>
<body>
    <div id="container"></div>
    <script>
        // Scene
        const scene = new THREE.Scene();

        // Camera
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 5;

        // Renderer
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('container').appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        // Cube Geometry and Materials
        const geometry = new THREE.BoxGeometry();
        const materials = [
            new THREE.MeshLambertMaterial({color: 0xff0000}), // Right face (+x)
            new THREE.MeshLambertMaterial({color: 0x00ff00}), // Left face (-x)
            new THREE.MeshLambertMaterial({color: 0x0000ff}), // Top face (+y)
            new THREE.MeshLambertMaterial({color: 0xffff00}), // Bottom face (-y)
            new THREE.MeshLambertMaterial({color: 0xff00ff}), // Front face (+z)
            new THREE.MeshLambertMaterial({color: 0x00ffff}), // Back face (-z)
        ];

        // Cube Mesh
        const cube = new THREE.Mesh(geometry, materials);
        scene.add(cube);

        // Camera Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Animation Loop
        function animate() {
            requestAnimationFrame(animate);

            // Continuous rotation
            cube.rotation.x += 0.01;
            cube.rotation.y += 0.01;

            // Update controls
            controls.update();

            // Render
            renderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', function() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
</html>
